sys_script_client.script:
function onLoad() {
   // console.log("‚úÖ onLoad triggered for Leave Request Form");

    if (!g_form.isNewRecord()) {
       // console.log("‚ÑπÔ∏è Existing record detected. Approver field remains unchanged.");
        return; // Stop execution for existing records
    }

    var userId = g_user.userID;
   // console.log(`üîç Logged-in User ID: ${userId}`);

    // Set "Requested By" field and make it read-only
    g_form.setValue('requested_by', userId);
    //g_form.setReadOnly('requested_by', true);
   // console.log("‚úÖ Requested By field set to logged-in user and made read-only.");

    // Fetch Approver (Manager or HR Manager) only for new records
    var ga = new GlideAjax('GetHRManagers');
    ga.addParam('sysparm_name', 'getUserManagerOrHR');
    ga.addParam('sysparm_user_id', userId);

    ga.getXMLAnswer(function(response) {
       // console.log("üîç Response from GetHRManagers Script Include:", response);

        if (response) {
            g_form.setValue('approver', response); // Set sys_id (not display name)
            //g_form.setReadOnly('approver', true); // Make read-only
            console.log(`‚úÖ Approver set to: ${response}`);
        } else {
           // console.warn("‚ö†Ô∏è No Manager or HR Manager found. Approver field remains empty.");
        }
    });
}
